import React from 'react'
const Donate = ()  => {
  const [email, setEmail] = React.useState('');
  const [password, setPas] = React.useState('');
  
  
  const submit = async () => {
    console.warn(email,password);
    const userId = JSON.parse(localStorage.getItem('user'))._id;
    let result = await fetch("http://localhost:4000/donation",{
      method: 'post',
      body: JSON.stringify({email, password, userId}),
      headers: {
        "Content-Type": "application/json"
      }
    });

    result = await result.json();
    console.warn(result);
    localStorage.setItem("user", JSON.stringify(result))
    

    console.warn(userId._id);
  }
  return (
  <>
     
      
        <div className='first'>
          <label htmlFor = "email">Email</label>
          <input type = "text" name='email' id='email' placeholder='Enter your email'
          value = {email} onChange = {(e) =>{setEmail(e.target.value)}}/>
        </div>

        <div>
        <label htmlFor = "password">Password</label>
          <input type = "text" name='password' id='password' placeholder='Enter your Password'
          value = {password} onChange = {(e) =>{setPas(e.target.value)}}/>

        </div>
        <button onClick={submit} type='submit'>LOG IN</button>
      
              

   
    </>
   
  );
}

export default Donate
