import {useState} from "react";
import React from 'react';
import wec from "../src/images/a7.png";


const Contact = () => {

  const [userData, setUserData] = useState({
    firstName:"",
    lastName:"",
    phone: "",
    email:"",
    address:"",
    message:"",
  });

  const [formErrors, setFormErrors] = useState({});
     
    let name, value;
     const postUserData = (event) =>{
       name = event.target.name;
       value = event.target.value;

       setUserData({...userData, [name]: value});

     };

    //  CONNECT WITH FIREBASE
    const submitData = async (event) => {
      event.preventDefault();
      setFormErrors(validate(userData));

      const { 
        firstName, lastName, phone, email, address, message }  = userData;
     const res = await fetch(
       
       'https://reactfirebasewebsite-e4798-default-rtdb.firebaseio.com/userDataRecords.json',
     {  
       
       method: "POST",
       headers: {
         "Content-Type" : "application/json",
       },
       body:JSON.stringify({

        firstName, lastName, phone, email, address, message


       }),
      }  
       
       );
       if(res){
         alert("Data Stored");
       } else {
         alert("Plz fill all the data");
       }

    };


    const validate = (value) => {

      const errors = {};
      const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
      if (!value.firstName){
        errors.firstName
      }

    }












  return (
      <>

      <section className='contactus-section'>
        <div className='container'>
          <div className='row'>
            <div className='col-12 col-lg-10 mx-auto'>
              <div className='row'>
                <div className="contact-leftside col-12 col-lg-5">
                  <h1 className='main-heading fw-bold'>
                    Contact Us
                  </h1>
                  <figure>
                    <img src={wec} alt="contactImg" className='img-fluid'/>
                  </figure>
                </div>

                {/* Right side of contact form */}

                <div className='contact-rightside col-12 col-lg-7'>
                
                  <form method='POST'>
                  <div className='row'>
                    <div className='col-12 col-lg-6 contact-input-field'>
                      <input
                       type="text" 
                       name='firstName'
                       id=''
                       className='form-control'

                       placeholder='First Name'
                       value={userData.firstName}
                       onChange = {postUserData}
                       />
                    </div>

                    <div className='col-12 col-lg-6 contact-input-field'>
                      <input
                       type="text" 
                       name='lastName'
                       id=''
                       className='form-control'
                       placeholder='Last Name'
                       value={userData.lastName}
                       onChange = {postUserData}
                       />
                    </div>

                  </div>



                  <div className='row'>
                    <div className='col-12 col-lg-6 contact-input-field'>
                      <input
                       type="text" 
                       name='phone'
                       id=''
                       className='form-control'

                       placeholder='Phone Number'
                       value={userData.phone}
                       onChange = {postUserData}
                       />
                    </div>

                    <div className='col-12 col-lg-6 contact-input-field'>
                      <input
                       type="text" 
                       name='email'
                       id=''
                       className='form-control'
                       placeholder='Email ID'
                       value={userData.email}
                       onChange = {postUserData}
                       />
                    </div>

                  </div>
                  
                  
                 <div className='row'>
                    <div className='col-12 contact-input-field'>
                      <input type="text" name='address' id='' className='form-control'
                       placeholder='Add Address'
                       value={userData.address}
                       onChange = {postUserData} />

                    </div>
                  </div>


                  <div className='row'>
                    <div className='col-12'>
                      <input type="text" name='message' id='' className='form-control' 
                      placeholder='Enter Your Message'
                      value={userData.message}
                       onChange = {postUserData} />

                    </div>
                  </div>

                  <div class="form-check form-checkbox-style ">
  <input class="form-check-input" type="checkbox" value="" id="flexCheckChecked"/>
  <label class="form-check-label" className='main-hero-para'>

  I agree that the Giver may contact me at the email address or phone number mentioned above
  </label>
</div>

  <button type='submit' className='btn-submit contact-input-field' onClick={submitData}>SUBMIT</button>

                  </form>
                </div>
            
              </div>
            </div>
          </div>
        </div>
      </section>
     
    </>
  );
  };

export default Contact
