    const userId = JSON.parse(localStorage.getItem('user'));
    let result = await fetch("http://localhost:5000/contact",{
      method: 'post',
      body: JSON.stringify({firstname, lastname, mobile, email, address, msg, userId}),
      headers: {
        "Content-Type": "application/json"
      }
    });

