            <div className='col-12 col-lg-6 donate-input-field'>
              <input
               type="text" 
               name='firstname'
               id='firstname'
               required
               autoComplete="off"
               className='form-control'

               placeholder='Enter your first name'
               value={firstname}
               onChange = {(e)=>{setFirstname(e.target.value)}}
               />
             
             {error && !firstname && <span className='error'>Enter valid first name </span>}
             </div>


             <div className='col-12 col-lg-6 contact-input-field'>
              <input
               type="text" 
               name='lastname'
               id='lastname'
               className='form-control'
               placeholder='Enter your last name.'
               value={mobile}
               onChange = {(e) =>{setLastname(e.target.value)}}
               />
                            {error && !lastname && <span className='error'>Enter valid last name </span>}

            </div>
           </div>








            <div className='col-12 col-lg-6 contact-input-field'>
              <input
               type="number" 
               name='mobile'
               id='mobile'
               className='form-control'
               placeholder='Enter your mobile no.'
               value={mobile}
               onChange = {(e) =>{setMobile(e.target.value)}}
               />
                            {error && !mobile && <span className='error'>Enter valid mobile number </span>}

            </div>
           </div>

           <div className='row'>
            <div className='col-12 col-lg-6 contact-input-field'>
              <input
               type="text" 
               name='email'
               id='email'
               className='form-control'
               placeholder='Enter your email'
               value={email}
               onChange = {(e) =>{setEmail(e.target.value)}}
               />
               {error && !email && <span className='error'>Enter valid emaid</span>}

            </div>
   
            
            
           

            <div className='row'>
            <div className='col-12  contact-input-field'>
              <input
               type="text" 
               name='Address'
               id='Address'
               className='form-control'
               placeholder='Enter your full address'
               value={address}
               onChange = {(e) =>{setAddress(e.target.value)}}
               />
                            {error && !username && <span className='error'>Enter valid Address </span>}

            </div>
               </div>

               <div className='row'>
            <div className='col-12 contact-input-field'>
              <input
               type="text" 
               name='foodq'
               id='foodq'
               className='form-control'
               placeholder='Enter your food quantity'
               value={foodq}
               onChange = {(e) =>{setFoodq(e.target.value)}}
               />
         {error && !foodq && <span className='error'>Enter valid name </span>}

            </div>    

          </div>



