          </figure>
        </div>
        

        {/* Right side of contact form */}

        <div className='contact-rightside col-12 col-lg-7'>
        
         
          <div className='row'>
            <div className='col-12 col-lg-6 donate-input-field'>
              <input
               type="text" 
               name='fullname'
               id='fullname'
               required
               autoComplete="off"
               className='form-control'

               placeholder='Enter Your Full Name'
               value={fullname}
               onChange = {(e)=>{setFullname(e.target.value)}}
               />
             
             {error && !fullname && <span className='error'>Enter valid name </span>}
             </div>

            <div className='col-12 col-lg-6 contact-input-field'>
              <input
               type="number" 
               name='mobile'
               id='mobile'
               className='form-control'
               placeholder='Enter your mobile no.'
               value={mobile}
               onChange = {(e) =>{setMobile(e.target.value)}}
               />
                            {error && !mobile && <span className='error'>Enter valid mobile number </span>}

            </div>
           </div>

           <div className='row'>
            <div className='col-12 col-lg-6 contact-input-field'>
              <input
               type="text" 
               name='email'
               id='email'
               className='form-control'
               placeholder='Enter your email'
               value={email}
               onChange = {(e) =>{setEmail(e.target.value)}}
               />
               {error && !email && <span className='error'>Enter valid emaid</span>}

            </div>
   
            <div className='col-12 col-lg-6 contact-input-field'>
              <input
               type="text" 
               name='state'
               id='state'
               className= 'form-control'
               placeholder='Select your state'
               value={state}
               onChange = {(e) =>{setState(e.target.value)}}
               />
                            {error && !state && <span className='error'>Enter valid state name </span>}

            </div>
            </div>
           

            <div className='row'>
            <div className='col-12  contact-input-field'>
              <input
               type="text" 
               name='Address'
               id='Address'
               className='form-control'
               placeholder='Enter your full address'
               value={address}
               onChange = {(e) =>{setAddress(e.target.value)}}
               />
                            {error && !fullname && <span className='error'>Enter valid Address </span>}

            </div>
               </div>

               <div className='row'>
            <div className='col-12 contact-input-field'>
              <input
               type="text" 
               name='age'
               id='age'
               className='form-control'
               placeholder='Enter your food quantity'
               value={age}
               onChange = {(e) =>{setAge(e.target.value)}}
               />
         {error && !age && <span className='error'>Enter valid age </span>}

            </div>    

          </div>



