import React from 'react'
// import web from "../src/images/m3.jpg";
// import wed from "../src/images/m1.jpg";
// import wem from "../src/images/m4.jpg";
 import wec from "../src/images/f22.jpg";
const Donate = ()  => {
  const [username, setUsername] = React.useState('');
  const [mobile, setMobile] = React.useState('');
  const [email, setEmail] = React.useState('');
  const [address, setAddress] = React.useState('');
  const [state, setState] = React.useState('');
  const [quantity, setQuantity] = React.useState('');
  const [dblanket, setBlanket] = React.useState('');
  const [dclothes, setClothes] = React.useState('');
  const [dshoes, setShoes] = React.useState('');
  const [dbooks, setBooks] = React.useState('');
  const [dnapkins, setNapkins] = React.useState('');
  const [dtoys, setToys] = React.useState('');
  const [error, setError] = React.useState(false);
  
  
  const donate = async () => {

    console.warn(!username);
    if(!username || !mobile || !email || !address || !dblanket || !dclothes || !dshoes || !dbooks || !dnapkins || !dtoys || !state || !quantity)
    {
      setError(true)
    return false;

    }

    console.warn(username, mobile, email, address, dblanket, dclothes, dshoes, dbooks, dnapkins, dtoys ,state, quantity);
    const userId = JSON.parse(localStorage.getItem('user'));
    let result = await fetch("http://localhost:4000/donation",{
      method: 'post',
      body: JSON.stringify({username, mobile, email, address, dblanket, dclothes, dshoes, dbooks, dnapkins, dtoys, quantity, state, userId}),
      headers: {
        "Content-Type": "application/json"
      }
    });

    result = await result.json();
    console.warn(result);
    // localStorage.setItem("user", JSON.stringify(result))
    // navigate('/')

    
  }
  return (
      <>



{/* <div className ="container-fluid main_header">
    <div className="row">
        <div className="col-md-10 col-12 mx-auto">
            <div className="row"> */}
          <section className='contactus-section'>
<div className='container'>
  <div className='row'>
    <div className='col-12 col-lg-10 mx-auto'>
      <div className='row'>
        <div className="contact-leftside col-12 col-lg-5">
          <h1 className='donate-h1 fw-bold'>
          DONATE BLANKET
          </h1>
           <figure>
            <img src={wec} alt="blanketImg" className='img-fluid'/>
          </figure> 
      
        </div>
    

        {/* Right side of contact form */}

        <div className='contact-rightside col-12 col-lg-7'>
        
         
          <div className='row'>
            <div className='col-12 col-lg-6 donate-input-field'>
              <input
               type="text" 
               name='username'
               id='username'
               required
               autoComplete="off"
               className='form-control'

               placeholder='Enter your full name'
               value={username}
               onChange = {(e)=>{setUsername(e.target.value)}}
               />
             
             {error && !username && <span className='error'>Enter valid name </span>}
             </div>

            <div className='col-12 col-lg-6 contact-input-field'>
              <input
               type="number" 
               name='mobile'
               id='mobile'
               className='form-control'
               placeholder='Enter your mobile no.'
               value={mobile}
               onChange = {(e) =>{setMobile(e.target.value)}}
               />
                            {error && !mobile && <span className='error'>Enter valid mobile number </span>}

            </div>
           </div>

           <div className='row'>
            <div className='col-12 col-lg-6 contact-input-field'>
              <input
               type="text" 
               name='email'
               id='email'
               className='form-control'
               placeholder='Enter your email'
               value={email}
               onChange = {(e) =>{setEmail(e.target.value)}}
               />
               {error && !email && <span className='error'>Enter valid emaid</span>}

            </div>
   
            <div className='col-12 col-lg-6 contact-input-field'>
              <input
               type="text" 
               name='state'
               id='state'
               className= 'form-control'
               placeholder='Enter your state'
               value={state}
               onChange = {(e) =>{setState(e.target.value)}}
               />
                            {error && !state && <span className='error'>Enter valid state name </span>}

            </div>
            </div>
           

            <div className='row'>
            <div className='col-12  contact-input-field'>
              <input
               type="text" 
               name='Address'
               id='Address'
               className='form-control'
               placeholder='Enter your full address'
               value={address}
               onChange = {(e) =>{setAddress(e.target.value)}}
               />
                            {error && !username && <span className='error'>Enter valid Address </span>}

            </div>
               </div>

               <div className='row'>
            <div className='col-12 contact-input-field'>

               <select class="form-select" aria-label="Default select example">
  <option selected>What you want to donate?</option> onChange = {(e) =>{setBlanket(e.target.value)}}
  <option value= {dblanket}> Donate Blanket</option> onChange = {(e) =>{setClothes(e.target.value)}}
  <option value={dclothes}>Donate Clothes</option> onChange = {(e) =>{setShoes(e.target.value)}}
  <option value={dshoes}>Donate Shoe</option> onChange = {(e) =>{setBooks(e.target.value)}}
  <option value={dbooks}>Donate Books</option> onChange = {(e) =>{setNapkins(e.target.value)}}
  <option value={dnapkins}>Donate Sanitary Napkins</option> onChange = {(e) =>{setToys(e.target.value)}}
  <option value={dtoys}>Donate Toys</option>
</select>

</div>
</div>

               <div className='row'>
            <div className='col-12 contact-input-field'>
              <input
               type="text" 
               name='quantity'
               id='quantity'
               className='form-control'
               placeholder='Enter the quantity of thing you wish to donate'
               value={quantity}
               onChange = {(e) =>{setQuantity(e.target.value)}}
               />
                            {error && !quan && <span className='error'>Enter valid name </span>}

            </div>


    </div>

        {/* <div className="contact-rightside"> */}
           

{/*           



          {/* <div className='row'>
            <div className='col-12 col-lg-6 contact-input-field'>
              <input
               type="number" 
               name='phone'
               id=''
               className='form-control'

               placeholder='Phone Number'
               value={userData.phone}
               onChange = {postUserData}
               />
            </div>

            <div className='col-12 col-lg-6 contact-input-field'>
              <input
               type="email" 
               name='email'
               id=''
               
               autoComplete="off"
               className='form-control'
               placeholder='Email ID'
               value={userData.email}
               onChange = {postUserData}
               />
            </div>

          </div>
          
          
         <div className='row'>
            <div className='col-12 contact-input-field'>
              <input type="text" name='address' id='' className='form-control'
               placeholder='Add Address'
               value={userData.address}
               onChange = {postUserData} />

            </div>
          </div>


          <div className='row'>
            <div className='col-12'>
              <input type="text" name='message' id='' className='form-control' 
              placeholder='Enter Your Message'
              value={userData.message}
               onChange = {postUserData} />

            </div>
          </div> */} 

          <div class="form-check form-checkbox-style ">
<input class="form-check-input" type="checkbox" value="" id="flexCheckChecked"/>
<label class="form-check-label" className='main-hero-para'>

I agree that the Giver may contact me at the email address or phone number mentioned above
</label>
</div>

<button onClick={donate} type = 'submit' className='btn-submit contact-input-field'>SUBMIT</button>

         
        </div>
        </div>
        </div>
        </div>
        </div>
  
        </section>
        
        
       
    
    
  </>
   
  );
}

export default Donate
